import { useEffect, useState } from 'react'
import { Router, Link, Route, Routes, useNavigate, useParams} from "react-router-dom";

const Register = () => {
        const [username, setUsername] = useState('')
        const [password, setPassword] = useState('')
        const [confirmedPassword, setConfirmedPassword] = useState('')


    const registerUser = async (name, pass) => {
        if(confirmedPassword === password) {
        try{
    const response = await fetch('https://fitnesstrac-kr.herokuapp.com/api/users/register', {
                    method: "POST",
                    headers: {
                    'Content-Type': 'application/json'
                        },
                    body: JSON.stringify({
                                username: name,
                                password: pass
        })
    })
        const result = await response.json();
        alert(result.message);
        setToken(result.token);
        return result;
        } catch(err) {
            console.error(err);
        }
    } else {
        alert("Oops passwords dont match");
    }
}


    return (
        <>
        <form className="d-flex justify-content-center flex-column" onSubmit= { (event) =>{
            event.preventDefault()
            registerUser(username, password)
            event.target.reset()

            }
        }>
            {/* <label htmlFor='username'>Username: </label> */}
            <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">@</span>
            <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="Username" onChange={(event) => {
                setUsername(event.target.value)
            }}></input><br/>
            </div>
            <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">@</span>
            <input type="text" class="form-control" placeholder="Password" aria-label="Password" aria-describedby="Password" onChange={(event) => {
                setPassword(event.target.value)
            }}></input><br/>
            </div>
            <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">@</span>
            <input type="text" class="form-control" placeholder="Confirm Password" aria-label="Confirm Password" aria-describedby="Confirm Password" onChange={(event) => {
                setConfirmedPassword(event.target.value)
            }}></input><br/>
            </div>
            <input type='submit' value='Create Account'></input>
        </form>
        <Link to={'/login'} className='btn btn-danger'>Ready to log in?</Link>
        </>
    )

}

export default Register
