import { Router, Link, Route, Routes, useNavigate, useParams } from "react-router-dom";
import { useEffect, useState } from 'react'

const View = (props) => {
    const { id } = useParams()
    const routines = props.routines
    const token = props.token
    const [name, setName] = useState('')
    const [description, setDescription] = useState('')
    const [count, setCount] = useState('')
    const [duration, setDuration] = useState('')
    console.log(props);
    // let singleRoutine = [];
    // setTimeout(() => {
    const singleRoutine = routines.find((routine) => {
        if(routine.id == id) {
                return routine;
        }
    })
    console.log(singleRoutine)

// }, 2000);

    console.log(routines)

    const addActivity = async (name, count, description, duration, token) => {
        try {
            const response = await fetch(`https://fitnesstrac-kr.herokuapp.com/api/routines/${singleRoutine.id}/activities`, {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({
                    name,
                    description,
                    count,
                    duration
                })
            })
            const result = await response.json()
        } catch (err) {
            console.error(err)
        }
    }
    const editActivity = async (name, description, count, duration) => {
        try {
            const response = await fetch(`https://fitnesstrac-kr.herokuapp.com/api/activities/${id}`, {
                method: "PATCH",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    name,
                    count,
                    duration,
                    description
                })
            })
            const result = await response.json()
        } catch (err) {
            console.error(err)
        }
    }
    const deleteActivity = async (id, token) => {
        try {
            await fetch(`https://fitnesstrac-kr.herokuapp.com/api/routines/${id}`, {
                method: "DELETE",
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
            })
            setRoutines((prev) =>
                prev.filter((routine) => id !== routine.id)
            )

        } catch (err) {
            console.error(err.message)
        }
    }

    const deleteRoutine = async (id, token) => {
        try {
            await fetch(`https://fitnesstrac-kr.herokuapp.com/api/routines/${id}`, {
                method: "DELETE",
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
            })
            setRoutines((prev) =>
                prev.filter((routine) => id !== routine.id)
            )

        } catch (err) {
            console.error(err.message)
        }
    }
    // setTimeout(() => {
        return  <>
        <h2>Routine: {singleRoutine.name}</h2>
        <h4 title={singleRoutine.name}>Goal: {singleRoutine.goal}</h4>
        <h2>Routine Exercises</h2>
        <div className="d-flex row flex-row border-success">
        {

            singleRoutine.activities ? singleRoutine.activities.map((activity) => {
                return <>
                <div className="col-sm-6">
                    <div className="card border-success shadow  mb-5 rounded" key={activity.title} style={{marginRight: '1em', marginLeft: '1em', marginBottom: '1em'}}>
                    <h5 className="card-header">{activity.name}</h5>
                    <div className="card-body" key={activity.id}>
                    <h4 className="card-text">Exercise description: {activity.description}</h4>
                    <h4 className="card-text">How long to do exercise: {activity.duration}</h4>
                    <h4 className="card-text">How many reps: {activity.count}</h4>

                    <label>Delete Exercise</label>
                    <input type='submit' value='Delete' className="btn btn-success btn-lg" onClick={() => {
                                                        token ? deleteRoutine()
                                                            : "not the owner";
                                                    }}></input>
                                                    </div>
                    </div>
                    </div>
                </>
            }) : <h4>No Activities</h4>
        }
            </div>

        {<form className="d-flex justify-content-center flex-column" onSubmit={(event) => {
            event.preventDefault()
            editActivity(name, description, count, duration, token)
            event.target.reset()
        }}>
            <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">Edit exercise name: </span>
            <input type="text" class="form-control" placeholder="edit exercise name" aria-label="Edit exercise name" aria-describedby="basic-addon1" onChange={(event) => {
                setName(event.target.value)
            }}></input><br/>
            </div>
                       <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">Edit exercise description: </span>
            <input type="text" class="form-control" placeholder="edit exercise description" aria-label="Edit exercise description" aria-describedby="basic-addon1" onChange={(event) => {
                setDescription(event.target.value)
            }}></input><br/>
            </div>
                                   <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">Edit how long to do exercise: </span>
            <input type="text" class="form-control" placeholder="edit time of exercise" aria-label="Edit time of exercise" aria-describedby="basic-addon1" onChange={(event) => {
                setDuration(event.target.value)
            }}></input><br/>
            </div>
             <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">Edit how many reps to do: </span>
            <input type="text" class="form-control" placeholder="edit number of reps" aria-label="Edit exercise reps" aria-describedby="basic-addon1" onChange={(event) => {
                setCount(event.target.value)
            }}></input><br/>
            </div>
            <input className="btn btn-success btn-lg" type='submit' value='Finish Edit'></input>
        </form>
        }
        {   <form className="d-flex justify-content-center flex-column" onSubmit={(event) => {
                event.preventDefault()
                addActivity(name, description, count, duration, token)
                event.target.reset()
            }}>
                <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">Add exercise to routine: </span>
            <input type="text" class="form-control" placeholder="name" aria-label="name" aria-describedby="basic-addon1" onChange={(event) => {
                setName(event.target.value)
            }}></input><br/>
            </div>
                <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">Add exercise description: </span>
            <input type="text" class="form-control" placeholder="description" aria-label="description" aria-describedby="basic-addon1" onChange={(event) => {
                setDescription(event.target.value)
            }}></input><br/>
            </div>
                <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">Add how long to do exercise: </span>
            <input type="text" class="form-control" placeholder="time per exercise" aria-label="time per exercise" aria-describedby="basic-addon1" onChange={(event) => {
                setDuration(event.target.value)
            }}></input><br/>
            </div>
                <div class="input-group mb-3">
            <span class="input-group-text" id="basic-addon1">Add how many reps to do: </span>
            <input type="text" class="form-control" placeholder="reps" aria-label="reps" aria-describedby="basic-addon1" onChange={(event) => {
                setCount(event.target.value)
            }}></input><br/>
            </div>
