import { useEffect, useState } from 'react'
import { Router, Link, Route, Routes, useNavigate, useParams, BrowserRouter } from "react-router-dom";


const Activities = (props) => {
    const [activities, setActivities] = useState([]);

const fetchActivities = async () => {
            await fetch('http://fitnesstrac-kr.herokuapp.com/api/activities', {
            headers: {
              'Content-Type': 'application/json',
            },
          }).then(response => response.json())
            .then(result => {
              setActivities(result);
            })
            .catch(console.error);
}
useEffect(() => {
    fetchActivities()
    console.log(activities)
}, [activities])

return <>
        {   <form className="d-flex justify-content-center flex-column" onSubmit={(event) => {
    event.preventDefault()
    addActivity(name, description, count, duration, token)
    event.target.reset()
}}>
    <div className="input-group mb-3">
<span className="input-group-text" id="basic-addon1">Add exercise to routine: </span>
<input type="text" className="form-control" placeholder="name" aria-label="name" aria-describedby="basic-addon1" onChange={(event) => {
    setName(event.target.value)
}}></input><br/>
</div>
    <div className="input-group mb-3">
<span className="input-group-text" id="basic-addon1">Add exercise description: </span>
<input type="text" className="form-control" placeholder="description" aria-label="description" aria-describedby="basic-addon1" onChange={(event) => {
    setDescription(event.target.value)
}}></input><br/>
</div>
    <div className="input-group mb-3">
<span className="input-group-text" id="basic-addon1">Add how long to do exercise: </span>
<input type="text" className="form-control" placeholder="time per exercise" aria-label="time per exercise" aria-describedby="basic-addon1" onChange={(event) => {
    setDuration(event.target.value)
}}></input><br/>
</div>
    <div className="input-group mb-3">
<span className="input-group-text" id="basic-addon1">Add how many reps to do: </span>
<input type="text" className="form-control" placeholder="reps" aria-label="reps" aria-describedby="basic-addon1" onChange={(event) => {
    setCount(event.target.value)
}}></input><br/>
</div>
<input className="btn btn-success btn-lg" type='submit' value='Add activity'></input>
</form>
}
{
    activities.map((activity) => {
return <div className="card border border-2 mt-3 bg-white shadow p-3 rounded border-success ms-3 me-5" key={activity.id}>
                             <div className="card-body border-2 bg-white " key={activity.id}>
                             <h3 className="card-text bg-white">{activity.name}</h3>
                             <h4 className="card-text bg-white" title={activity.description}>Exercise Description: {activity.description}</h4>
                                 <input type='button' value='delete' className="btn btn-danger ms-2" id={activity.id} onClick={(event) => {
                                     event.preventDefault()
                                     deleteActivity(activity.id, token)
                                 }}></input>
                             </div>

 <div className="accordion" id="accordionExample">
 <div className="accordion-item">
 <h2 className="accordion-header" id="headingOne">
 <button className="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
 Edit Activity
 </button>
 </h2>
 <div id="collapseOne" className="accordion-collapse collapse show" aria-labelledby="headingOne" data-bs-parent="#accordionExample">
 <div className="accordion-body">
 <form className="d-flex justify-content-center flex-column bg-white" onSubmit={(event) => {
event.preventDefault()
editActivity(name, description, count, duration, token)
event.target.reset()
}}>
                     <div className="bg-dark p-4">
<div className="input-group mb-3">
<span className="input-group-text" id="basic-addon1">Edit exercise name: </span>
<input type="text" className="form-control" placeholder="edit exercise name" aria-label="Edit exercise name" aria-describedby="basic-addon1" onChange={(event) => {
setName(event.target.value)
}}></input><br/>
</div>
    <div className="input-group mb-3">
<span className="input-group-text" id="basic-addon1">Edit exercise description: </span>
<input type="text" className="form-control" placeholder="edit exercise description" aria-label="Edit exercise description" aria-describedby="basic-addon1" onChange={(event) => {
setDescription(event.target.value)
}}></input><br/>
</div>
                <div className="input-group mb-3">
<span className="input-group-text" id="basic-addon1">Edit how long to do exercise: </span>
<input type="text" className="form-control" placeholder="edit time of exercise" aria-label="Edit time of exercise" aria-describedby="basic-addon1" onChange={(event) => {
setDuration(event.target.value)
}}></input><br/>
</div>
<div className="input-group mb-3">
<span className="input-group-text" id="basic-addon1">Edit how many reps to do: </span>
<input type="text" className="form-control" placeholder="edit number of reps" aria-label="Edit exercise reps" aria-describedby="basic-addon1" onChange={(event) => {
setCount(event.target.value)
}}></input><br/>
</div>
<input className="btn btn-success btn-lg" type='submit' value='Finish Edit'></input>
</div>
 </form>
 </div>
 </div>
 </div>
 </div>
 </div>
    })
}
</>
}

export default Activities;
